import { Button, message, Modal, Image } from 'antd';
import React, { useMemo, useState } from 'react';
import './index.less';

interface PopupBoxType {
  data: any;
}

export default function PopupBox(props: PopupBoxType) {
  const { data } = props;
  const [visibleImg, setVisibleImg] = useState(false);
  const seeImgHandle = () => {
    if (imgUrl) {
      setVisibleImg(true);
    } else {
      setVisibleImg(false);
      message.warning('暂无图片地址');
    }
  };

  const imgUrl = useMemo(() => {
    if (data && data.type === '筑坝情况') {
      return data.data.picaid;
    } else {
      return '';
    }
  }, [data]);
  console.log(imgUrl, data);

  const onError = () => {
    message.error('图片加载失败,请重新尝试');
    setVisibleImg(false);
  };
  return (
    <>
      <div className="popup-box">
        <div className="title">{data.type}</div>
        <div className="popup-box-content global-scroll">
          {Object.keys(data.colums).map((keys: string) => {
            if (keys === 'picaid') return;
            return (
              <div className="popup-item" key={keys}>
                <span className="name">{data.colums[keys]}：</span>
                <span className="value">{data.data[keys]}</span>
              </div>
            );
          })}
        </div>
        {data.type === '筑坝情况' && (
          <Button className="click-img" onClick={seeImgHandle}>
            查看图片
          </Button>
        )}
        {visibleImg && data.type === '筑坝情况' && (
        <Image.PreviewGroup
          preview={{
            visible: visibleImg,
            onVisibleChange: (vis) => setVisibleImg(vis)
          }}
        >
          <Image
            className="popup-img"
            fallback="data:image/png;base64,"
            preview={{ visible: false }}
            onError={onError}
            src={imgUrl}
            // src="https://img2.baidu.com/it/u=1814268193,3619863984&fm=253&fmt=auto&app=138&f=JPEG"
          />
        </Image.PreviewGroup>
      )}
      </div>
      
    </>
  );
}
